import { useState, createContext } from 'react'

const CartButtonContext = () => {

  const ctx = createContext({
    cartIsOpen: false,
    setCartIsOpen: (isOpen) => {isOpen ? openCartHandler : closeCartHandler}
  })

  return ctx
}

export default CartButtonContext
