import { delay, call } from 'redux-saga/effects';

function* AnimationSaga() {
  yield call({type: 'SET_BTN_TRUE'})
  console.log("hit 1")
  // yield delay(300);
  yield call({type: 'SET_BTN_FALSE'})
  console.log("hit 2")
}

export default AnimationSaga
