// import { useContext } from 'react';
import { createStore, applyMiddleware } from 'redux';
import createSagaMiddleware from 'redux-saga'

// import CartContext from './cart-context'
// cartBtnCtx.setCartIsOpen()

import rootSaga from '../sagas/root'

const initialState = { btnIsHighlighted: false , cart: {}};


export const AnimationReducer = (state = initialState, action) => {

  switch (action.type) {
    case 'SET_BTN_TRUE':
      console.log("true")
      return { btnIsHighlighted: true, cart: state.cart }
    case 'SET_BTN_FALSE':
      console.log("false")
      return { btnIsHighlighted: false, cart: state.cart }
    case 'ANIMATE_CART':
      return state
    case 'UPDATE_CART':
      return { btnIsHighlighted: state.btnIsHighlighted, cart: action.payload}
    case 'FETCH_CART':
      return state
    default:
      return state
  }
  // if (action.type === 'ADD_ANIM') {
  //   return {
  //     btnIsHighlighted: !state.btnIsHighlighted
  //   };
  // }
  // return state;
};

const sagaMiddleware = createSagaMiddleware();
const store = createStore(AnimationReducer, applyMiddleware(sagaMiddleware));
sagaMiddleware.run(rootSaga)

export default store;
