import { createContext } from 'react'

const [cartIsOpen, setCartIsOpen] = useState(false);

const openCartHandler = () => {
  setCartIsOpen(true);
}

const closeCartHandler = () => {
  setCartIsOpen(false);
}

const CartButtonContext = () => createContext({
  cartIsOpen: false,
})


export default CartButtonContext
