import rootSaga from '../sagas/root'

const initialState = { btnIsHighlighted: false };

const postCartHandler = async (userData) => {

  await fetch('https://saga-quick-attempt-default-rtdb.firebaseio.com/meals.json', {
    method: 'POST',
    body: JSON.stringify({
      user: userData,
      orderedItems: cartCtx.items
    }),
  })
  setIsSubmitting(false);
  setDidSubmit(true);
  cartCtx.ClearCart();
}

export const AnimationReducer = (state = initialState, action) => {

  switch (action.type) {
    case 'SET_BTN_TRUE':
      console.log("true")
      return { btnIsHighlighted: true }
    case 'SET_BTN_FALSE':
      console.log("false")
      return { btnIsHighlighted: false }
    case 'UPDATE_CART':
      return state
    case 'FETCH_CART':
      return state
    default:
      return state
  }
  // if (action.type === 'ADD_ANIM') {
  //   return {
  //     btnIsHighlighted: !state.btnIsHighlighted
  //   };
  // }
  // return state;
};

const sagaMiddleware = createSagaMiddleware();
const store = createStore(AnimationReducer, applyMiddleware(sagaMiddleware));
sagaMiddleware.run(rootSaga)

export default store;
