import { all, takeE } from 'redux-saga/effects'

import AnimationSaga from './AnimationSaga'

// notice how we now only export the rootSaga
// single entry point to start all Sagas at once
export default function* rootSaga() {
  yield all([
    AnimationSaga()
  ])
}
