import { all, call, takeEvery } from 'redux-saga/effects'
import axios from 'axios'

const fetchMeals = async () => {
  const response = await fetch(
    'https://saga-quick-attempt-default-rtdb.firebaseio.com/meals.json'
  );

  if (!response.ok) {
    throw new Error('Something went wrong!');
  }

  const responseData = await response.json();
  console.log(JSON.stringify(responseData))
}


import animationSaga from './AnimationSaga'
// import { getCart } from './GetCartSaga'

// notice how we now only export the rootSaga
// single entry point to start all Sagas at once
export default function* rootSaga() {
  yield takeEvery('UPDATE_CART', animationSaga)
  const { response } = yield call(axios, 'https://saga-quick-attempt-default-rtdb.firebaseio.com/meals.json')
  console.log("Saga: ")
  console.log(JSON.stringify(response))
  // yield takeEvery('GET_FIREBASE', getCart)
}



